import macCrest from './images/mcmaster/macCrest.png';
import './App.css';
import team from "./images/team.jpg";

function App() {

  const Navbar = () => {

    const navLinks = [
      {
        name: "Home",
        href: "/",
        icon: null,
      },
      {
        name: "Demo",
        href: "/demo",
        icon: null,
      },
      {
        name: "GitHub",
        href: "https://github.com",
        icon: null,
      },
    ]

    return (
        <div className="w-full bg-white flex">
            <div className="w-6/12 pl-6 flex py-3 gap-4">
                <div>
                  <img src={macCrest} className="w-10" />
                </div>
                <div className="flex flex-col">
                  <h1 className="text-lg text-slate-800 leading-6">2022-23 Capstone Project</h1>
                  <h1 className="text-md text-slate-500">by Nihal Azavedo, Tinson Chen, Joe Furfaro</h1>
                </div>
            </div>
            <div className="w-6/12 pr-6 flex flex-row-reverse">
              {navLinks.reverse().map(l => {
                return l.href != window.location.pathname+window.location.search ?
                <a className="text-center w-24 pt-5 text-slate-800 hover:text-slate-500" href={l.href}>
                  {l.name}
                </a> :
                <a className="text-center w-24 border-b-4 font-bold border-mac-maroon pt-5 text-mac-maroon" href={l.href}>
                {l.name}
              </a>
              })}
            </div>
        </div>
    );
  }

  const Button = ({text, href, icon="", iconSide="left", primary=true}) => {
    return (
      <a href={href} className={"inline-block px-8 tracking-wide rounded-lg py-3 font-bold shadow-md " + (primary ? "bg-mac-maroon hover:bg-mac-maroon-darker text-red-100 hover:text-red-200" : "bg-slate-200 hover:bg-slate-100 text-slate-500 hover:text-slate-600")}>
        <div className={"flex items-baseline gap-2 " + (iconSide === "left" ? "flex-row" : "flex-row-reverse")}>
            {icon != "" ? <i className={"fa-solid " + icon}></i> : null}
            <p>{text.toUpperCase()}</p>
        </div>
      </a>
    );
  }

  return (
    <>
      <Navbar />
      <div className="w-full py-14 bg-slate-100">
        <div className="z-10 w-1000 mx-auto rounded-lg bg-white flex shadow-xl">
          <div className="w-6/12 px-8 py-12 rounded-l-lg border-l-4 border-slate-300">
            <h1 className="text-2xl text-slate-800 font-bold tracking-wider">WELCOME TO OUR PROJECT</h1>
            <p className="mt-4 leading-7 text-slate-700 mb-7">
              Cras eget arcu mollis, facilisis odio eu, fringilla sem. Pellentesque at vulputate dolor. Vivamus felis odio, auctor id placerat sed, feugiat in nisi. Curabitur a lorem vitae tortor fermentum hendrerit. Maecenas at dui luctus, porta lectus in, consequat enim. Sed et dolor commodo, aliquet nibh non, volutpat eros.  Curabitur a lorem vitae tortor fermentum hendrerit. Maecenas at dui luctus, porta lectus in, consequat enim. Sed et dolor commodo, aliquet nibh non, volutpat eros.
            </p>
            <Button text="Try It Out" href="/demo" icon="fa-circle-chevron-right" iconSide="right" />
          </div>
          <div className="w-6/12 rounded-r-lg">
            <img src={team} className="w-full rounded-r-lg" />
          </div>
        </div>
        <svg className="z-0 absolute w-full h-screen mt-24" xmlns="http://www.w3.org/2000/svg">
        <polygon points={`0,0 ${window.innerWidth},250 ${window.innerWidth},510 0,510`} style={{fill:"lime"}} />
        </svg>

      </div>
    </>
  );
}

export default App;
