const UploadButton = ({text, onChange, icon="", iconSide="left", primary=true, scheme="maroon"}) => {

    const inner = <div className={"flex items-baseline gap-2 " + (iconSide === "left" ? "flex-row" : "flex-row-reverse")}>
                      {icon != "" ? <i className={"fa-solid " + icon}></i> : null}
                      <p>{text.toUpperCase()}</p>
                  </div>

    if(primary) {
      if(scheme === "maroon")
        return (
          <input type="file" value={inner} onChange={onChange} className="inline-block px-8 tracking-wide rounded-lg py-3 font-bold shadow-md bg-mac-maroon hover:bg-mac-maroon-darker text-red-100 hover:text-red-200" />
        );
      else if(scheme === "purple")
        return (
          <input type="file" value={inner} onChange={onChange} className="inline-block px-8 tracking-wide rounded-lg py-3 font-bold shadow-md bg-mac-purple hover:bg-mac-purple-darker text-pink-100 hover:text-pink-200" />
        );
    } else {
      return (
        <input type="file" value={inner} onChange={onChange} className="inline-block px-8 tracking-wide rounded-lg py-3 font-bold shadow-md bg-slate-200 hover:bg-slate-100 text-slate-500 hover:text-slate-600" />
      );
    }


}

export default UploadButton;
